import { Component } from "react";

export default class classComponent extends Component {
  constructor() {
    super();
    this.state = {
      arrayChecklist: [
        "Buy groceries for next week",
        "Renew car insurance",
        "Sign up for online course edit test",
      ],
    };
  }

  //   checklistItems = () => {
  //     var arrayChecklist = [];
  //     arrayChecklist = [
  //       "Buy groceries for next week",
  //       "Renew car insurance",
  //       "Sign up for online course edit test",
  //     ];

  render() {

    var displayItems = this.state.arrayChecklist.map((arrayChecklist) => {
      return (
        <div>
          <label className="styled-checkbox" for="item1">
            {arrayChecklist}
            <input type="checkbox" id="item1" name="item1" value="item1" />
            <span className="checkmark"></span>
          </label>
          <div className="icons-group">
            <i className="fas fa-pen icon icon-pen tooltip">
              <span className="tooltiptext">Edit todo</span>
            </i>
            <i className="fas fa-trash-alt icon icon-trash tooltip">
              <span className="tooltiptext">Delete todo</span>
            </i>
            <div className="date">
              <i className="fas fa-info-circle tooltip">
                <span className="tooltiptext">Created date</span>
              </i>
              28 Jun 2020
            </div>
          </div>
        </div>
      );
    });

    return (
      <div class="main-container__checklist-items">
        <form>
          <div className="checklist-row">
            <div className="form-group">{displayItems};</div>
          </div>
        </form>
      </div>
    );
  }
}

